animation.onupdate:
let M         = mat4.create();

mat4.translate(M, M, [0, -67780, 0]);
mat4.multiply(M, M, this.animations['iss-orbit-inverse'].M);
mat4.rotate(M, M, +23.5*Math.PI/180, [0,1,0]);
mat4.rotate(M, M, -51.6*Math.PI/180, [0,1,0]);
mat4.translate(M, M, [0, +67780, 0]);

mat4.rotate(M, M, 90*Math.PI/180, [0,0,1]);
mat4.rotate(M, M, 90*Math.PI/180, [0,1,0]);

let animation = arguments[0];
mat4.multiply(M, M, animation.M);
let oldPos    = this.userdata['initial-sun-light-position'];
let oldPosVec = vec4.fromValues(oldPos[0], oldPos[1], oldPos[2], 0);
let newPos    = vec4.create();
vec4.transformMat4(newPos, oldPosVec, M);
this.bindings['sun-light-position'].value = [newPos[0], newPos[1], newPos[2]];
